import { useEffect, useRef, useState } from "react";
import { NativeSyntheticEvent, StyleSheet, TextInput, TextInputChangeEventData, View } from "react-native";

type OTPProps = {
    length: number;
};

export const OTP = (props: OTPProps) => {
    const {length} = props;
    const [inputs, setInputs] = useState<string[]>(new Array(length).fill(""));
    const [focusedIndex, setFocusedIndex] = useState(0);
    //const [inputRefs, setInputRefs] = useState([]);

    // useEffect(() => {
    //     let refs = Array.from(Array(length).keys()).map(entry => {
    //         return useRef();
    //     });


    // }, [length])

    const handleSetInputs = (index: number, value: string) => {
        let newInputArray = [...inputs];
        newInputArray[index] = value;
        setInputs(newInputArray);
        setFocusedIndex(index);
    }

    return (
        <View style={{flexDirection: 'row'}}>
            {new Array(length).fill("").map((_, index) => {
                // const handleInputFocus = (element: ) => {
                //     if (elmnt.key === "Delete" || elmnt.key === "Backspace") {
                //       const next = elmnt.target.tabIndex - 2;
                //       if (next > -1) {

                //         elmnt.target.form.elements[next].focus()
                //       }
                //     }
                //     else {
                //       console.log("next");

                //         const next = elmnt.target.tabIndex;
                //         if (next < 5) {
                //           elmnt.target.form.elements[next].focus()
                //         }
                //     }

                //   }

                return (
                    <SingleOTPElement
                        key={`OTP-${index}`}
                        value={inputs[index]}
                        //onKeyPress={this.keyPressed}
                        onChangeText={value => handleSetInputs(index, value)}
                        maxLength={1}
                        focused={focusedIndex === index}
                    />
                );
            })}
        </View>
    );
}

type SingleOTPElementProps = {
    value: string;
    onChangeText: (value: string) => void;
    maxLength: number;
    focused: boolean;
};

const SingleOTPElement = (props: SingleOTPElementProps) => {
    const { value, onChangeText, maxLength, focused } = props;
    const inputRef = useRef();
    //const [focused, setFocused] = useState(false);
    return (
        <TextInput
            value={value}
            onChangeText={onChangeText}
            maxLength={maxLength}
            keyboardType="numeric"
            style={focused ? styles.SingleOTPElementFocused : styles.SingleOTPElement }
            // onFocus={() => setFocused(true)}
            // onBlur={() => setFocused(false)}
        />
    );
}

const styles = StyleSheet.create({
	SingleOTPElement: {
        height: 50,
        width: 50,
        margin: 2,
        borderStyle: 'solid',
        borderWidth: 2,
        borderColor: 'black'
	},
    SingleOTPElementFocused: {
        height: 50,
        width: 50,
        margin: 2,
        borderStyle: 'solid',
        borderWidth: 2,
        borderColor: 'white'
	}
});
