import { ActivityIndicator, Dimensions, ScrollView, StyleSheet } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

import { Text, View } from '../components/Themed';
import authService from '../services/authService';
import { OTP } from '../components/OTP';

export default function MagicCodeVerifyScreen(props: { route: { params: any } }) {
	const {width} = Dimensions.get('window');
	const verifyMagicCode = async (code: number) => {
		const response = await authService.verifyMagicCode(code);
		console.log(response);
	}

	return (
		<ScrollView
			keyboardShouldPersistTaps='handled'
			contentInsetAdjustmentBehavior="automatic"
			contentContainerStyle={{ flexGrow: 1 }}
		>
			<LinearGradient colors={['#0086A4', '#240071']} start={{ x: 1, y: 0 }} end={{ x: 0, y: 1 }} style={styles.container}>
				<View style={styles.body}>
					<View style={[styles.panel, {width: width * 0.8}]}>
						<Text style={styles.verifyCode}>
							Verify Code
							<ActivityIndicator size="small" color="#0086A4" style={{marginStart: 10}} />
						</Text>
						<OTP pinCount={5} triggerAction={(code) => verifyMagicCode(code)} />
					</View>
				</View>
			</LinearGradient>
		</ScrollView>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		alignItems: 'center',
		height: '100%',
		padding: 20
	},
	title: {
		color: 'white',
		fontSize: 45,
		fontWeight: 'bold',
		letterSpacing: 8,
		marginTop: 80,
		fontFamily: 'Roboto'
	},
	titleBody: {
		color: 'white',
		fontSize: 16,
		marginTop: 30,
		fontFamily: 'Roboto',
		width: 300,
		textAlign: 'center',
		flex: 1
	},
	spacer: {
		minHeight: 300,
		alignItems: 'center',
		backgroundColor: 'transparent'
	},
	center: {
		flex: 1,
		backgroundColor: 'transparent',
		justifyContent: 'center'
	},
	flexChild: {
		flex: 1
	},
	body: {
		flex: 1,
		backgroundColor: 'transparent',
		justifyContent: 'center',
	},
	panel: {
		maxWidth: 500,
		padding: 20,
		backgroundColor: 'white',
		borderRadius: 10
	},
	verifyCode: {
		fontSize: 30,
		color: '#0086A4',
		textAlign: 'center',
		marginBottom: 20
	}
});
