import { StyleProp, StyleSheet, TouchableOpacity, ViewStyle } from 'react-native';
import { Text, TextProps } from './Themed';

type ButtonProps = {
  style:  StyleProp<ViewStyle>
  value?: string;
  buttonType: 'solid' | 'border'
  onPress: () => void;
}

export const Button: React.FC<ButtonProps> = (props) => {
  const {style, value, buttonType, children, onPress} = props;
  let buttonStyle = styles.button;
  if (buttonType === 'border') {
    buttonStyle = Object.assign({borderColor: '#53C08C', borderWidth: 2}, buttonStyle);
  } else if (buttonType === 'solid') {
    buttonStyle = Object.assign({backgroundColor: '#53C08C'}, buttonStyle);
  }
  return (
    <TouchableOpacity style={[style, buttonStyle]} onPress={onPress}>
      {value && <Text style={styles.buttonText}>{value}</Text>}
      {!value && children && {children}}
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  button: {
    height: 50,
    borderRadius: 10,
    width: 270,
    paddingHorizontal: 20,
    alignItems: 'center',
    justifyContent: 'center'
  },
  buttonText: {
    color: '#53C08C',
    fontWeight: 'bold'
  }
});
