import { MutableRefObject, useEffect, useLayoutEffect, useRef, useState } from "react";
import { NativeSyntheticEvent, StyleSheet, TextInput, TextInputChangeEventData, TouchableWithoutFeedback, View } from "react-native";
import usePrevious from "../hooks/usePrevious";

type OTPProps = {
    pinCount: number;
};

export const OTP = (props: OTPProps) => {
    const {pinCount} = props;
    const [otpValue, setOtpValue] = useState<string[]>(new Array(pinCount).fill(""));
    const [selectedIndex, setSelectedIndex] = useState(0);
    const [inputRefs, setInputRefs] = useState<(TextInput | null)[]>(new Array(pinCount).fill(null));

    const handleChangeText = (index: number, value: string) => {
        console.log('hit');
        let newOtpValue = [...otpValue];
        newOtpValue[index] = value;
        setOtpValue(newOtpValue);
        if (index < inputRefs.length) {
            focusField(index+1);
        } else {
            blurAllFields();
        }
        // setFocusedIndex(index);
    }

    const focusField = (index: number) => {
        if (index < inputRefs.length) {
            console.log('focusField');
            setTimeout(() => (inputRefs[index] as TextInput).focus(), 0)
            setSelectedIndex(index);
        }
    }

    const blurAllFields = () => {
        inputRefs.forEach((field: TextInput | null) => setTimeout(() => (field as TextInput).focus(), 0))
        setSelectedIndex(-1);
    }

    const handleKeyPressTextInput = (index: number, key: string) => {
        console.log(key);
        if (key === 'Backspace') {
            if (!otpValue[index] && index > 0) {
                handleChangeText(index - 1, '')
                focusField(index - 1)
            }
        }
    }

    const SingleOTPElement = (props: SingleOTPElementProps) => {
        const { focused, index, inputRefs, ...rest } = props;

        return (
            <TextInput
                ref={ref => inputRefs[index] = ref}
                keyboardType="numeric"
                style={focused ? styles.SingleOTPElementFocused : styles.SingleOTPElement }
                onKeyPress={({ nativeEvent: { key } }) => { handleKeyPressTextInput(index, key) }}
                textContentType="oneTimeCode"
                maxLength={1}
                {...rest}
            />
        );
    }

    return (
        <View style={{flexDirection: 'row'}}>
            <TouchableWithoutFeedback
                onPress={() => {
                    let filledPinCount = otpValue.filter((digit) => { return (digit !== null && digit !== undefined && digit !== "") }).length;
                    console.log(Math.min(filledPinCount, pinCount - 1));
                    focusField(Math.min(filledPinCount, pinCount - 1))
                }}
            >
                <>
                {new Array(pinCount).fill("").map((_, index) => {
                    // const handleInputFocus = (element: ) => {
                    //     if (elmnt.key === "Delete" || elmnt.key === "Backspace") {
                    //       const next = elmnt.target.tabIndex - 2;
                    //       if (next > -1) {

                    //         elmnt.target.form.elements[next].focus()
                    //       }
                    //     }
                    //     else {
                    //       console.log("next");

                    //         const next = elmnt.target.tabIndex;
                    //         if (next < 5) {
                    //           elmnt.target.form.elements[next].focus()
                    //         }
                    //     }

                    //   }

                    return (
                        <SingleOTPElement
                            key={`OTP-${index}`}
                            inputRefs={inputRefs}
                            index={index}
                            value={otpValue[index]}
                            onChangeText={value => handleChangeText(index, value)}
                            focused={selectedIndex === index}
                        />
                    );
                })}
                </>
            </TouchableWithoutFeedback>
        </View>
    );
}

type SingleOTPElementProps = {
    value: string;
    index: number,
    onChangeText: (value: string) => void;
    inputRefs: (TextInput | null) [],
    focused: boolean;
};

const styles = StyleSheet.create({
	SingleOTPElement: {
        height: 50,
        width: 50,
        margin: 2,
        borderStyle: 'solid',
        borderWidth: 2,
        borderColor: 'black'
	},
    SingleOTPElementFocused: {
        height: 50,
        width: 50,
        margin: 2,
        borderStyle: 'solid',
        borderWidth: 2,
        borderColor: 'white'
	}
});
