import { StyleProp, StyleSheet, TouchableOpacity, ViewStyle } from 'react-native';
import { Text, TextProps } from './Themed';

type ButtonProps = {
  style:  StyleProp<ViewStyle>
  value?: string;
  outline?: boolean;
  onPress: () => void;
}

export const Button: React.FC<ButtonProps> = (props) => {
  const {style, value, outline, children, onPress} = props;
  let buttonStyle = styles.button;
  let textStyle;
  if (outline) {
    buttonStyle = Object.assign({borderColor: '#53C08C', borderWidth: 2}, buttonStyle);
    textStyle = Object.assign({}, styles.buttonText);
    textStyle.color = '#53C08C'
  } else {
    buttonStyle = Object.assign({backgroundColor: '#53C08C', borderWidth: 0}, buttonStyle);
  }

  return (
    <TouchableOpacity style={[style, buttonStyle]} onPress={onPress}>
      {value && <Text style={textStyle}>{value}</Text>}
      {!value && children && {children}}
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  button: {
    height: 50,
    borderRadius: 10,
    width: 287,
    paddingHorizontal: 20,
    alignItems: 'center',
    justifyContent: 'center'
  },
  buttonText: {
    color: 'white',
    fontWeight: 'bold'
  }
});
