import { StatusBar } from 'expo-status-bar';
import { Platform, StyleSheet } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

import { Text, View } from '../components/Themed';
import { Input } from '../components/Input';
import { useState } from 'react';

export default function MessagesScreen() {
  const [phoneNumber, setPhoneNumber] = useState("");
  return (
      <LinearGradient colors={['#0086A4', '#005C71']} start={{x: 1, y: 0}} end={{x: 0,  y: 1}} style={styles.container}>
        <Text style={styles.title}>NearBye</Text>
        <Text style={styles.titleBody}>An anonymous proximity-based social platform</Text>
        <Input placeholder='Phone number' value={phoneNumber} onChangeText={setPhoneNumber} style={{marginVertical: 20}} />
        <StatusBar style={Platform.OS === 'ios' ? 'light' : 'auto'} />
      </LinearGradient>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
  },
  title: {
    fontSize: 45,
    fontWeight: 'bold',
    letterSpacing: 8,
    marginTop: 80,
    fontFamily: 'Roboto'
  },
  titleBody: {
    fontSize: 16,
    marginTop: 30,
    fontFamily: 'Roboto',
    width: 300,
    textAlign: 'center'
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
});
