import { StatusBar } from 'expo-status-bar';
import { KeyboardAvoidingView, Platform, StyleSheet } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

import { Text, View } from '../components/Themed';
import { Input } from '../components/Input';
import { useState } from 'react';
import { Button } from '../components/Button';
import { KeyboardAwareScrollView } from 'react-native-keyboard-aware-scroll-view';
import { SafeAreaView } from 'react-native-safe-area-context';

export default function MessagesScreen() {
  const [phoneNumber, setPhoneNumber] = useState("");
  return (
        <LinearGradient colors={['#0086A4', '#005C71']} start={{x: 1, y: 0}} end={{x: 0,  y: 1}} style={styles.container}>
          <KeyboardAvoidingView
            style={styles.flexChild}>
            <SafeAreaView style={styles.flexChild}>
              <View style={styles.spacer}>
                <Text style={styles.title}>NearBye</Text>
                <Text style={styles.titleBody}>An anonymous proximity-based social platform</Text>
              </View>
              <View style={styles.center}>
                <Input selectTextOnFocus keyboardType='phone-pad' placeholder='Phone number' value={phoneNumber} onChangeText={setPhoneNumber} maxLength={14} />
                <Button style={{marginTop: 40}} value='Continue' onPress={() => {}} />
              </View>
              <View style={styles.spacer} />
            </SafeAreaView>
          </KeyboardAvoidingView>

        </LinearGradient>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
  },
  title: {
    fontSize: 45,
    fontWeight: 'bold',
    letterSpacing: 8,
    marginTop: 80,
    fontFamily: 'Roboto'
  },
  titleBody: {
    fontSize: 16,
    marginTop: 30,
    fontFamily: 'Roboto',
    width: 300,
    textAlign: 'center',
    flex: 1
  },
  spacer: {
    minHeight: 300,
    alignItems: 'center',
    backgroundColor: 'transparent'
  },
  center: {
    flex: 1,
    backgroundColor: 'transparent',
    justifyContent: 'center'
  },
  flexChild: {
    flex: 1
  }
});
