import { StyleProp, StyleSheet, TouchableOpacity, ViewStyle } from 'react-native';
import { Text, TextProps } from './Themed';

type ButtonProps = {
  style:  StyleProp<ViewStyle>
  value?: string;
  outline?: boolean;
  onPress: () => void;
}

export const Button: React.FC<ButtonProps> = (props) => {
  const {style, value, outline, children, onPress} = props;

  return (
    <TouchableOpacity style={[style, button, outline ? styles.buttonOutline : styles.buttonSolid]} onPress={onPress}>
      {value && <Text style={outline ? styles.buttonOutlineText : styles.buttonSolidText}>{value}</Text>}
      {!value && children && {children}}
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  button: {
    height: 50,
    borderRadius: 10,
    width: 287,
    paddingHorizontal: 20,
    alignItems: 'center',
    justifyContent: 'center'
  },
  buttonSolid: {
    backgroundColor: '#53C08C'
  },
  buttonOutline: {
    borderColor: '#53C08C',
    borderWidth: 2
  },
  buttonSolidText: {
    color: 'white',
    fontWeight: 'bold'
  },
  buttonOutlineText: {
    color: 'white',
    fontWeight: 'bold'
  }
});
