import { ScrollView, StyleSheet } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

import { Text, View } from '../components/Themed';
import { Input } from '../components/Input';
import { useEffect, useState } from 'react';
import { Button } from '../components/Button';
import authService from '../services/authService';

type MagicLinkParams = {
	code: string
}

export default function MagicLinkVerifyScreen(props: { route: { params: MagicLinkParams } }) {
	const { code } = props.route.params;

	const verifyMagicLinkCode = async () => {
		const response = await authService.verifyMagicLinkCode(code);
		console.log(response);
	}

	useEffect(() => {
		verifyMagicLinkCode();
	}, []);

	return (
		<ScrollView
			keyboardShouldPersistTaps='handled'
			contentInsetAdjustmentBehavior="automatic"
			contentContainerStyle={{ flexGrow: 1 }}
		>
			<LinearGradient colors={['#0086A4', '#240071']} start={{ x: 1, y: 0 }} end={{ x: 0, y: 1 }} style={styles.container}>
				<Text>Verify</Text>
			</LinearGradient>
		</ScrollView>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		alignItems: 'center',
		height: '100%',
		padding: 20
	},
	title: {
		color: 'white',
		fontSize: 45,
		fontWeight: 'bold',
		letterSpacing: 8,
		marginTop: 80,
		fontFamily: 'Roboto'
	},
	titleBody: {
		color: 'white',
		fontSize: 16,
		marginTop: 30,
		fontFamily: 'Roboto',
		width: 300,
		textAlign: 'center',
		flex: 1
	},
	spacer: {
		minHeight: 300,
		alignItems: 'center',
		backgroundColor: 'transparent'
	},
	center: {
		flex: 1,
		backgroundColor: 'transparent',
		justifyContent: 'center'
	},
	flexChild: {
		flex: 1
	}
});
