import { useEffect, useRef, useState } from "react";
import { NativeSyntheticEvent, StyleSheet, TextInput, TextInputChangeEventData, View } from "react-native";

type OTPProps = {
    length: number;
};

export const OTP = (props: OTPProps) => {
    const {length} = props;
    const [inputs, setInputs] = useState<string[]>([]);
    const [numericInputs, setNumericInputs] = useState<number[]>([]);
    //const [inputRefs, setInputRefs] = useState([]);

    // useEffect(() => {
    //     let refs = Array.from(Array(length).keys()).map(entry => {
    //         return useRef();
    //     });


    // }, [length])

    const handleSetInputs = (index: number, event: NativeSyntheticEvent<TextInputChangeEventData>) => {
        let newInputArray = [...inputs];
        let newNumericInputArray = [...numericInputs];

        console.log(event.target);

        newInputArray[index] = event.target.toString();
        newNumericInputArray[index] = event.target;

        setInputs(newInputArray);
        setNumericInputs(newNumericInputArray);
    }

    return (
        <View style={{flexDirection: 'row'}}>
            {Array.from(Array(length).keys()).map(entry => {
                // const handleInputFocus = (element: ) => {
                //     if (elmnt.key === "Delete" || elmnt.key === "Backspace") {
                //       const next = elmnt.target.tabIndex - 2;
                //       if (next > -1) {

                //         elmnt.target.form.elements[next].focus()
                //       }
                //     }
                //     else {
                //       console.log("next");

                //         const next = elmnt.target.tabIndex;
                //         if (next < 5) {
                //           elmnt.target.form.elements[next].focus()
                //         }
                //     }

                //   }

                return (
                    <SingleOTPElement
                        key={`OTP-${entry}`}
                        value={inputs[entry]}
                        //onKeyPress={this.keyPressed}
                        onChange={e => handleSetInputs(entry, e)}
                        maxLength={1}
                    />
                );
            })}
        </View>
    );
}

type SingleOTPElementProps = {
    value: string;
    onChange: (e: NativeSyntheticEvent<TextInputChangeEventData>) => void;
    maxLength: number;
};

const SingleOTPElement = (props: SingleOTPElementProps) => {
    const inputRef = useRef();
    return (
        <TextInput
            {...props}
            keyboardType="numeric"
            style={styles.SingleOTPElement}
        />
    );
}

const styles = StyleSheet.create({
	SingleOTPElement: {
        height: 80,
        width: 80,
        margin: 2,
        borderStyle: 'solid',
        borderWidth: 2
	}
});
