import { AxiosResponse } from "axios";

export const handleResponse = (response: AxiosResponse<any, any>) => {
    console.log('response: ', response);
    if (response.status === 200) {
        return {
            success: response.data
        }
    } else {
        return {
            error: response.statusText
        }
    }
}
