import { AxiosResponse } from "axios";

export const handleResponse = (promise: Promise<AxiosResponse<any, any>>) => {
    promise
        .then((response) => {
            if (response.status === 200) {
                return {
                    success: response.data
                }
            } 
        })
        .catch((err) => {

            console.log(err);
        });
}
