import { MutableRefObject, useEffect, useLayoutEffect, useRef, useState } from "react";
import { NativeSyntheticEvent, StyleSheet, TextInput, TextInputChangeEventData, View } from "react-native";
import usePrevious from "../hooks/usePrevious";

type OTPProps = {
    length: number;
};

export const OTP = (props: OTPProps) => {
    const {length} = props;
    const [inputs, setInputs] = useState<string[]>(new Array(length).fill(""));
    const [selectedIndex, setSelectedIndex] = useState(0);
    const [inputRefs, setInputRefs] = useState<(TextInput | null)[]>(new Array(length).fill(null));

    // useEffect(() => {
    //     let refs = Array.from(Array(length).keys()).map(entry => {
    //         return useRef();
    //     });


    // }, [length])

    const handleSetInputs = (index: number, value: string) => {
        let newInputArray = [...inputs];
        newInputArray[index] = value;
        setInputs(newInputArray);
        if (index === (length-1)) {
            blurAllFields();
        } else {
            focusField(index);
        }
        // setFocusedIndex(index);
    }

    const focusField = (index: number) => {
        if (index < inputRefs.length) {
            (inputRefs[index] as TextInput).focus();
            setSelectedIndex(index);
        }
    }

    const blurAllFields = () => {
        inputRefs.forEach((field: TextInput | null) => (field as TextInput).blur())
        setSelectedIndex(-1);
    }

    return (
        <View style={{flexDirection: 'row'}}>
            {new Array(length).fill("").map((_, index) => {
                // const handleInputFocus = (element: ) => {
                //     if (elmnt.key === "Delete" || elmnt.key === "Backspace") {
                //       const next = elmnt.target.tabIndex - 2;
                //       if (next > -1) {

                //         elmnt.target.form.elements[next].focus()
                //       }
                //     }
                //     else {
                //       console.log("next");

                //         const next = elmnt.target.tabIndex;
                //         if (next < 5) {
                //           elmnt.target.form.elements[next].focus()
                //         }
                //     }

                //   }

                return (
                    <SingleOTPElement
                        key={`OTP-${index}`}
                        inputRefs={inputRefs}
                        index={index}
                        value={inputs[index]}
                        onChangeText={value => handleSetInputs(index, value)}
                        maxLength={1}
                        focused={selectedIndex === index}
                    />
                );
            })}
        </View>
    );
}

type SingleOTPElementProps = {
    value: string;
    index: number,
    onChangeText: (value: string) => void;
    maxLength: number;
    inputRefs: (TextInput | null) [],
    focused: boolean;
};

const SingleOTPElement = (props: SingleOTPElementProps) => {
    const { focused, index, inputRefs, ...rest } = props;

    return (
        <TextInput
            ref={ref => inputRefs[index] = ref}
            keyboardType="numeric"
            style={focused ? styles.SingleOTPElementFocused : styles.SingleOTPElement }
            {...rest}
        />
    );
}

const styles = StyleSheet.create({
	SingleOTPElement: {
        height: 50,
        width: 50,
        margin: 2,
        borderStyle: 'solid',
        borderWidth: 2,
        borderColor: 'black'
	},
    SingleOTPElementFocused: {
        height: 50,
        width: 50,
        margin: 2,
        borderStyle: 'solid',
        borderWidth: 2,
        borderColor: 'white'
	}
});
